    private fun parseGPO(data: ByteArray): Boolean {
        data.toTlvObjects()?.firstOrNull {
            it.tag == 0x77L
        }?.also {
            it.value.toTlvObjects()?.forEach {
//                cardData[it.tag] = it
                if (cardData[it.tag] == null) {
                    cardData[it.tag] = it
                } else {
//                    lastError = "4"
                    println(it.tag.toString(16))
                    return false
                }

            }
        } ?: return false
        return true
    }




